import axios from 'axios'
import React, { useEffect, useState } from 'react'
import url from './host'
import { Button, Form, Modal, message } from 'antd';
import { PlusOutlined } from '@ant-design/icons';
export default function Carousel() {
var [carusel_image,setCarouselImage]=useState([])


function getCarousel() {
    axios.get(`${url}/api/carousel`).then(res=>{
setCarouselImage(res.data)
    })
}
useEffect(()=>{
getCarousel()
},[])
const [isModalOpen, setIsModalOpen] = useState(false);
const showModal = () => {
  setIsModalOpen(true);
};
const handleOk = () => {
var data =new FormData()
data.append("image",document.querySelector('#modal_data_file').value)
axios.post(`${url}/api/carousel`,data).then(res=>{
    handleCancel()
    getCarousel()
}).catch(err=>{
    message.err
})
};
const handleCancel = () => {
  setIsModalOpen(false);
};
  return (
    <div>
        <br />
        <Button type="primary" onClick={showModal}>
        Create Carousel
      </Button>





      <Modal title="Basic Modal" open={isModalOpen} onOk={handleOk} onCancel={handleCancel}>
     <Form>
     <Form.Item label="Image" valuePropName="fileList">
    <div className='file_input' listType="picture-card"> 
      <input type="file" className='get_datainput' id="modal_data_file" />
            <button
              style={{
                border: 0,
                background: 'none',
              }}
              type="button"
            >
              <PlusOutlined />
              <div
                style={{
                  marginTop: 8,
                }}
              >
                Upload
              </div>
            </button>
          </div>
        </Form.Item>
     </Form>
      </Modal>
    </div>
  )
}
