import React, { useEffect, useState } from 'react'
import {
  Button,
  Cascader,
  DatePicker,
  Form,
  Input,
  InputNumber,
  Mentions,
  Modal,
  Select,
  TreeSelect,
  Upload,
  message,
} from 'antd';
import { PlusOutlined } from '@ant-design/icons';
import axios from 'axios';
import url from './host';
const { RangePicker } = DatePicker;
const formItemLayout = {
  labelCol: {
    xs: {
      span: 24,
    },
    sm: {
      span: 6,
    },
  },
  wrapperCol: {
    xs: {
      span: 24,
    },
    sm: {
      span: 14,
    },
  },
};
export default function Home() {
  var [company,setCompany]=useState()
  const [isModalOpen, setIsModalOpen] = useState(false);
  const showModal = () => {
    setIsModalOpen(true);
  };
  var [file1,setFile]=useState()
  const handleFileChange = (info) => {
    const { fileList } = info;
    setFile(fileList)
  };

const handleOk = () => {
var data=new FormData()
console.log(formItemLayout);
data.append("phone",document.querySelector('.InputNumber').value )
data.append("email",document.querySelector('.InputEmail').value )
data.append("address",document.querySelector(".InputAddress").value )
data.append("facebook",document.querySelector('.InputFacebook').value )
data.append("lan",document.querySelector('.InputLanguation').value )
data.append("lac",document.querySelector(".InputLocation").value )
data.append("telegram",document.querySelector('.InputTelegram').value )
data.append("youtobe",document.querySelector(".InputYoutube").value )
data.append("instagram",document.querySelector('.InputInstagram').value )
data.append("image", file1[0])

axios.post(`${url}/api/company`,data).then(res=>{
  message.success("Companiya yaratildi")
  handleCancel()
}).catch(err=>{
  message.error("xato qayta urunib ko`ring")
handleCancel()
})
  };
  const handleCancel = () => {
    setIsModalOpen(false);
  };
  const getCompany=()=>{
axios.get(`${url}/api/compamy`).then(res=>{
setCompany(res.data)
}).catch(err=>{
  message.error("not get Company data")
})
  }
useEffect(()=>{
getCompany()
},[])



  return (
    <div>
      {data.le}
      <Button type="primary" onClick={showModal}>
      Create Company
      </Button>
      <Modal title="Create Company" open={isModalOpen} onOk={handleOk} onCancel={handleCancel}>
      <Form
    {...formItemLayout}
    variant="filled"
    style={{
      maxWidth: 600,
    }}
  >
    <Form.Item
      label="Address"
      name="InputAddress"
      rules={[
        {
          required: true,
          message: 'Iltimos manzilni kiriting!',
        },
      ]}
    >
      <Input className='InputAddress' />
    </Form.Item>

    <Form.Item
      label="Email"
      name="InputEmail"
      rules={[
        {
          required: true,
          message: 'Iltimos emailni kiriting!',
        },
      ]}
    >
      <Input className='InputEmail' />
    </Form.Item>
    <Form.Item
      label="Facebook"
      name="InputFacebook"
      rules={[
        {
          required: true,
          message: 'Iltimos linkni kititing!',
        },
      ]}
    >
      <Input className='InputFacebook' />
    </Form.Item>
    <Form.Item
      label="Telegram"
      name="InputTelegram"
      rules={[
        {
          required: true,
          message: 'Iltimos linkni kititing!',
        },
      ]}
    >
      <Input className='InputTelegram' />
    </Form.Item>
    <Form.Item
      label="Youtobe"
      name="InputYoutube"
      rules={[
        {
          required: true,
          message: 'Iltimos linkni kititing!',
        },
      ]}
    >
      <Input className='InputYoutube' />
    </Form.Item>
    <Form.Item
      label="Instagram"
      name="InputInstagram"
      rules={[
        {
          required: true,
          message: 'Iltimos linkni kititing!',
        },
      ]}
    >
      <Input className='InputInstagram' />
    </Form.Item>
    <Form.Item
      label="Languation"
      name="InputLanguation"
      rules={[
        {
          required: true,
          message: 'Iltimos linkni kititing!',
        },
      ]}
    >
      <Input className='InputLanguation' />
    </Form.Item>
    <Form.Item
      label="Location"
      name="InputLocation"
      rules={[
        {
          required: true,
          message: 'Iltimos linkni kititing!',
        },
      ]}
    >
      <Input className='InputLocation' />
    </Form.Item>
    <Form.Item
      label="Phone"
      name="InputNumber"
      rules={[
        {
          required: true,
          message: 'Iltimos raqamni kititing!',
        },
      ]}
    >
      <Input
        style={{
          width: '100%',
        }}
         type='number'
        className='InputNumber'
      />
    </Form.Item>
    <Form.Item label="Upload" valuePropName="fileList">
          <Upload className='file' onChange={(e)=>handleFileChange(e)} action="/upload.do" listType="picture-card">
            <button
              style={{
                border: 0,
                background: 'none',
              }}
              type="button"
            >
              <PlusOutlined />
              <div
                style={{
                  marginTop: 8,
                }}
              >
                Upload
              </div>
            </button>
          </Upload>
        </Form.Item>
  </Form>
      </Modal>

    </div>
  )
}
